/**
* A Credential is assigned to all daemons, at bare minimum it contains
* a Daemon's name, pid & a secret string generated by Dae so to verify a
* Daemon.
* 
* NOTE: On subsequent calls to `Dae::scan()`, credentials will be uupdated to include
* the CommonTCP port used by a deamon.
* @private
*/
class Credential {
  constructor(props) {
    const {name, pid, port, secret} = props;
    this.name = name;
    this.pid = pid;
    this.port = port;
    this.secret = secret;
    this.created = Date.now();
  }
}

module.exports = Credential;
